import ReviewList from "./ReviewList";
import { useEffect, useState } from "react";
import { deleteReview, getReviews, updateReview } from "../api";
import ReviewForm from "./ReviewForm";
import { createReview } from "../api";
import useAsync from "../../hooks/useAsync";

const LIMIT = 6;

function App() {
    //최신순, 베스트순
    const [order, setOrder] = useState("createdAt");
    //삭제 함수(선택 된 거 빼고 items로 바꿔줌.)
    const [items, setItems] = useState([]);
    const sortedItems = items.sort((a, b) => b[order] - a[order]);

    const [offset, setOffset] = useState(0);
    const [hasNext, setHasNext] = useState(false);

    const [isLoading, loadingError, getReviewsAsync] = useAsync(getReviews);

    const handleNewestClick = () => setOrder("createdAt");
    const handleBestClick = () => setOrder("rating");

    const handleLoadMore = () => {
        handleLoad({ order, offset, limit: LIMIT });
    };
    const handleDelete = async (id) => {
        const result = await deleteReview(id);
        if (!result) return;
        //비동기로 결과를 참조하니까 setter함수는 콜백으로 사용해야 한다.
        // const nextItems = items.filter((item) => item.id !== id);
        // setItems(nextItems);
        setItems((prevItems) => items.filter((item) => item.id !== id));
    };

    //리뷰 데이터 불러오고 세팅
    const handleLoad = async (options) => {
        let result = await getReviewsAsync(options);
        if (!result) return;

        const { reviews, paging } = result;

        if (options.offset === 0) {
            setItems(reviews);
        } else {
            setItems((prevItems) => [...prevItems, ...reviews]);
        }
        setOffset(options.offset + reviews.length);
        setHasNext(paging.hasNext);
    };

    //비동기로 일어나니까 콜백 함수로 일어나야함.
    const handleCreateSuccess = (review) => {
        setItems((prevItems) => [review, ...prevItems]);
    };

    const handleUpdateSuccess = (review) => {
        setItems((prevItems) => {
            const splitIdx = prevItems.findIndex(
                (item) => item.id === review.id
            );
            return [
                //slice() 메서드는 어떤 배열의 begin 부터 end 까지(end 미포함)에 대한
                //복사본을 새로운 배열 객체로 반환합니다. 원본 배열은 바뀌지 않습니다.
                ...prevItems.slice(0, splitIdx),
                review,
                ...prevItems.splice(splitIdx + 1),
            ];
        });
    };

    //이렇게 되면 handleLoad가 실행되면 setItems가 실행되고, setItems가 실해오되면, App이 재실행 되기 된다.
    //App이 재실행되면, handleload가 다시 실행되고 무한 반복이 된다.
    //useEffect안에 handleload를 넣어주자.
    useEffect(() => {
        handleLoad({ order, offset: 0, limit: LIMIT });
    }, [order]);

    return (
        <div>
            <div>
                <button onClick={handleNewestClick}>최신순</button>
                <button onClick={handleBestClick}>베스트순</button>
            </div>
            <ReviewForm
                onSubmit={createReview}
                onSubmitSuccess={handleCreateSuccess}
            />
            <ReviewList
                items={sortedItems}
                onDelete={handleDelete}
                onUpdate={updateReview}
                onUpdateSuccess={handleUpdateSuccess}
            />
            {hasNext && (
                <button disabled={isLoading} onClick={handleLoadMore}>
                    더보기
                </button>
            )}
            {loadingError?.mesage && <span>{loadingError.mesage}</span>}
        </div>
    );
}

export default App;
